mapper.select:
SELECT a.id, a.imgname, a.imgurl, b.username, a.updatetime, a.sizes, a.abnormal, a.source, a.imgtype, a.explains, a.md5key, a.imguid, a.format, a.about, a.violation, a.idname FROM imgdata a LEFT JOIN user b ON a.userid = b.id where 1=1
and a.source = #{source}
and a.updatetime >= #{starttime}
and a.updatetime <= #{stoptime}
and a.violation !=""
and a.userid = #{userid}
and a.userid in ( SELECT id from `user` where username LIKE '%${username}%')
and a.userid not in ( SELECT id from `user` where username LIKE '%${username}%')
and a.imguid = #{imguid}
and idname LIKE '%${idname}%'
order by a.id desc

mapper.update:
UPDATE imgdata


            abnormal=#{abnormal}


            explains=#{explains}


            idname=#{idname}


     where imgname=#{imgname}

mapper.select:
SELECT DISTINCT  YEAR(updatetime) yyyy FROM `imgdata` where
IFNULL(updatetime,'')

    and userid = #{userid}

SELECT count(id) countNum, MONTH(updatetime) monthNum FROM imgdata where YEAR(updatetime)=#{yyyy}
and userid = #{userid}
GROUP BY MONTH(updatetime) ORDER BY MONTH(updatetime) ASC
